(1, 'Recent Posts', 'post-recent', 'Loads up the last 5 posts', 'system-collection-post-search', '{\"sort\": {\"post_published\": \"DESC\"}, \"range\": 5, \"filter\": {\"post_active\": 1, \"post_public\": 1, \"post_status\": \"approved\"} }', '2019-02-03 14:02:34', '2019-02-12 11:29:05', '<div class=\"box\">\r\n    <div class=\"box-head box-purple\">\r\n        <div class=\"box-title\">{{_ \'Recent Posts\'}}</div>\r\n    </div>\r\n    <div class=\"box-body box-padding\">\r\n        {{#each rows}}\r\n            <div class=\"post-link\">\r\n                <a href=\"/post/detail/{{post_detail}}\">{{post_title}}</a>\r\n            </div>\r\n        {{/each}}\r\n    </div>\r\n</div>', 1),
(2, 'Recent Comments', 'post-comments', 'Loads up the last 5 comments', 'post-comment-search', '{\"sort\": {\"comment_created\": \"DESC\"}, \"range\": 5, \"filter\": {\"comment_active\": 1}}', '2019-02-03 15:11:07', '2019-02-12 11:29:15', '<div class=\"box\">\r\n    <div class=\"box-head box-purple\">\r\n        <div class=\"box-title\">{{_ \'Recent Comments\'}}</div>\r\n    </div>\r\n    <div class=\"box-body box-padding\">\r\n        {{#each rows}}\r\n            <div class=\"post-link mb-4\">\r\n                <a href=\"/post/detail/{{post_slug}}#comment-{{comment_id}}\">{{profile_first_name}} on <strong>{{post_title}}</strong></a>\r\n            </div>\r\n        {{/each}}\r\n    </div>\r\n</div>', 1),
(3, 'Post Archives', 'post-archives', 'Posts organized by year and month', 'post-archive-search', '{}', '2019-02-07 22:51:25', '2019-02-07 22:51:25', '<div class=\"box\">\r\n    <div class=\"box-head box-purple\">\r\n        <div class=\"box-title\">{{_ \'Archives\'}}</div>\r\n    </div>\r\n    <div class=\"box-body box-padding\">\r\n        {{#each rows}}\r\n            <div class=\"post-link\">\r\n                <a href=\"/post/archives/{{year}}/{{month}}\">{{date ./date \'F Y\'}}</a>\r\n            </div>\r\n        {{/each}}\r\n    </div>\r\n</div>', 1),
(4, 'Post Categories', 'post-categories', 'Loads up categories that are linked with posts', 'post-category-search', NULL, '2019-02-12 11:34:51', '2019-02-12 11:34:51', '<div class=\"box\">\r\n    <div class=\"box-head box-purple\">\r\n        <div class=\"box-title\">{{_ \'Categories\'}}</div>\r\n    </div>\r\n    <div class=\"box-body box-padding\">\r\n        {{#each rows}}\r\n            <div class=\"category-link\">\r\n                <a href=\"/post/category/{{category_slug}}\">{{category_title}}</a>\r\n            </div>\r\n        {{/each}}\r\n    </div>\r\n</div>', 1);
