(1, 'Custom Website', '', 'Incept is a low code framework. Open Source.', '[\"cms\", \"php8\"]', 1, '2019-02-03 10:24:20', '2019-02-10 13:45:43', NULL, NULL, '<h1>My Site</h1><p>You can edit me in the admin</p>', '/images/default-image.png', 'app', 'text/html'),
(2, 'About Us', 'about-us', 'Incept is a low code framework. Open Source.', '[\"about incept\"]', 1, '2019-02-03 10:28:50', '2019-02-10 13:45:53', NULL, NULL, '<h1>About Me</h1><p>This is a page about me...</p>', '/images/default-image.png', 'app', 'text/html'),
(3, 'Post Detail', 'post/detail/:post_slug', 'SEO and Template for Post schema', '[\"post\"]', 1, '2019-02-04 02:39:56', '2019-02-12 11:31:36', NULL, NULL, '<style>\r\n    .page-post .body {\r\n        padding: 0;\r\n    }\r\n\r\n    .page-post .post-banner {\r\n        max-height: 400px;\r\n        overflow: hidden;\r\n    }\r\n\r\n    .page-post .post-category {\r\n        text-transform: uppercase;\r\n    }\r\n\r\n    .page-post .post-file-title,\r\n    .page-post .post-comment-title {\r\n        border-bottom: 1px solid #DDDDDD;\r\n        text-transform: uppercase;\r\n    }\r\n    \r\n    .comment-form label {\r\n        font-weight: bold;\r\n    }\r\n    \r\n    .comment-form .form-action {\r\n        text-align: right;\r\n    }\r\n</style>\r\n\r\n<main class=\"post-container\">\r\n    {{#if post.post_banner}}\r\n        <div class=\"post-banner\">\r\n            <img src=\"{{post.post_banner}}\" width=\"100%\" />\r\n        </div>\r\n    {{/if}}\r\n    {{#if post.category}}\r\n        <div class=\"post-category-list p-2 bg-light\">\r\n            <div class=\"container\">\r\n                {{#each post.category}}\r\n                    <a class=\"post-category\" href=\"/post/category/{{category_slug}}\">\r\n                        {{category_title}}\r\n                    </a>\r\n                    {{#unless @last}} :: {{/unless}}\r\n                {{/each}}\r\n            </div>\r\n        </div>\r\n    {{/if}}\r\n    <div class=\"container\">\r\n        <div class=\"row mt-3\">\r\n            <div class=\"col-md-8\">\r\n                <h1 class=\"post-title\">{{post.post_title}}</h1>\r\n\r\n                <div class=\"post-info\">\r\n                    <em class=\"profile-name\">{{_ \'by: %s\' post.profile_name}}</em>\r\n                    <em class=\"post-published\">{{relative post.post_published}}</em>\r\n                </div>\r\n\r\n                <div class=\"post-detail mt-4\">\r\n                    {{{post.post_detail}}}\r\n                </div>\r\n\r\n                {{#if post.file}}\r\n                    <h3 class=\"post-file-title mt-5 pb-2\">{{_ \'Files\'}}</h3>\r\n                    <table class=\"table table-striped\">\r\n                        <tbody>\r\n                            {{#each post.file}}\r\n                                <tr class=\"post-file\">\r\n                                    <td>\r\n                                        <a href=\"/file/{{file_id}}/{{file_name}}\">\r\n                                            {{file_name}}\r\n                                        </a>\r\n                                    </td>\r\n                                    <td>\r\n                                        {{file_description}}\r\n                                    </td>\r\n                                </tr>\r\n                            {{/each}}\r\n                        </tbody>\r\n                    </table>\r\n                {{/if}}\r\n\r\n                {{#if post.comment}}\r\n                    <h3 class=\"post-comment-title mt-5 pb-2\">{{_ \'Comments\'}}</h3>\r\n                    <div class=\"post-comment-list\">\r\n                        {{#each post.comment}}\r\n                            <div id=\"comment-{{comment_id}}\" class=\"post-comment\">\r\n                                <div class=\"comment-info\">\r\n                                    <em class=\"profile-name\">\r\n                                        {{#if profile_website}}\r\n                                            <a href=\"{{profile_website}}\">{{profile_name}}</a>\r\n                                        {{else}}\r\n                                            {{profile_name}}\r\n                                        {{/if}}\r\n                                    </em>\r\n                                    <em class=\"comment-created\">{{relative comment_created}}</em>\r\n                                </div>\r\n                                <div class=\"comment-detail mt-4\">\r\n                                    {{{markdown comment_detail}}}\r\n                                </div>\r\n                            </div>\r\n                        {{/each}}\r\n                    </div>\r\n\r\n                    <h4 class=\"mt-5\">Add a Comment</h4>\r\n                    <form class=\"comment-form\" id=\"comment-form\" method=\"post\">\r\n                        <div class=\"form-group{{#if errors.comment_detail}} has-error{{/if}}\">\r\n                            <label>{{_ \'Comment\'}}*</label>\r\n                            <div>\r\n                                <textarea class=\"form-control\" name=\"comment_detail\">{{{request comment_detail}}}</textarea>\r\n                            </div>\r\n                            {{#if errors.comment_detail}}\r\n                                <div class=\"help-text\">{{errors.comment_detail}}</div>\r\n                            {{/if}}\r\n                        </div>\r\n                        {{#request \'session\' \'me\'}}\r\n                        {{else}}\r\n                            <div class=\"form-group{{#if ../errors.profile_name}} has-error{{/if}}\">\r\n                                <label>{{_ \'Name\'}}*</label>\r\n                                <div>\r\n                                    <input class=\"form-control\" name=\"profile_name\" placeholder=\"eg. John Doe\" type=\"text\" value=\"{{../profile_name}}\" />\r\n                                </div>\r\n                                {{#if ../errors.profile_name}}\r\n                                    <div class=\"help-text\">{{../errors.profile_name}}</div>\r\n                                {{/if}}\r\n                            </div>\r\n                            <div class=\"form-group{{#if ../errors.profile_email}} has-error{{/if}}\">\r\n                                <label>{{_ \'Email\'}}*</label>\r\n                                <div>\r\n                                    <input class=\"form-control\" name=\"profile_email\" placeholder=\"eg. john@acme.com\" type=\"email\" value=\"{{../profile_email}}\" />\r\n                                </div>\r\n                                {{#if ../errors.profile_email}}\r\n                                    <div class=\"help-text\">{{../errors.profile_email}}</div>\r\n                                {{/if}}\r\n                            </div>\r\n                            <div class=\"form-group{{#if ../errors.profile_website}} has-error{{/if}}\">\r\n                                <label>{{_ \'Website\'}}</label>\r\n                                <div>\r\n                                    <input class=\"form-control\" name=\"profile_website\" placeholder=\"eg. http://acme.com\" type=\"url\" value=\"{{../profile_website}}\" />\r\n                                </div>\r\n                                {{#if ../errors.profile_website}}\r\n                                    <div class=\"help-text\">{{../errors.profile_website}}</div>\r\n                                {{/if}}\r\n                            </div>\r\n                        {{/request}}\r\n                        <div class=\"form-action\">\r\n                            <button class=\"btn btn-primary\">Post Comment</button>\r\n                        </div>\r\n                    </form>\r\n                {{/if}}\r\n            </div>\r\n            <div class=\"col-md-4\">\r\n                {{{block \'post-recent\'}}}\r\n\r\n                {{{block \'post-comments\'}}}\r\n\r\n                {{{block \'post-archives\'}}}\r\n\r\n                {{{block \'post-categories\'}}}\r\n            </div>\r\n        </div>\r\n    </div>\r\n</main>\r\n', '/images/default-image.png', 'app', 'text/html'),
(4, 'Post Search', 'post/search', 'This page is for showing search results for posts.', '[\"post search\"]', 1, '2019-02-04 06:07:12', '2019-02-12 11:46:56', 'system-collection-search', '{\"sort\": {\"post_published\": \"DESC\"}, \"filter\": {\"post_active\": 1, \"post_status\": \"approved\"}, \"schema\": \"post\"}', '<style>\r\n    .post-container .post-published {\r\n        font-size: 14px;\r\n        font-style: normal;\r\n        font-weight: bold;\r\n        text-transform: uppercase;\r\n    }\r\n</style>\r\n\r\n<main class=\"post-container\">\r\n    <div class=\"container\">\r\n        <div class=\"row mt-3\">\r\n            <div class=\"col-md-8\">\r\n                <h1>\r\n                    {{#if q}}\r\n                        {{_ \'Results for: %s\' q}}\r\n                    {{else}}\r\n                        {{_ \'All Posts\'}}\r\n                    {{/if}}\r\n                </h1>\r\n\r\n                {{#each page.results.rows}}\r\n                    <hr />\r\n                    <em class=\"post-published\">{{date post_published \'F d, Y\'}}</em>\r\n                    <h3 class=\"post-title mt-2\">\r\n                        <a class=\"post-link\" href=\"/post/detail/{{post_slug}}\">\r\n                            {{post_title}}\r\n                        </a>\r\n                    </h3>\r\n                    <p class=\"post-summary\">{{post_summary}}</p>\r\n                {{/each}}\r\n            </div>\r\n            <div class=\"col-md-4\">\r\n                {{{block \'post-recent\'}}}\r\n\r\n                {{{block \'post-archives\'}}}\r\n            </div>\r\n        </div>\r\n    </div>\r\n</main>\r\n', '/images/default-image.png', 'app', 'text/html'),
(5, 'Post Category', 'post/category/:category_slug', 'SEO and Template for Category schema', '[\"post\"]', 1, '2019-02-10 14:00:44', '2019-02-12 11:46:23', 'post-category-detail', NULL, '<style>\r\n    .body {\r\n        padding: 0;\r\n    }\r\n\r\n    .category-container .category-banner {\r\n        max-height: 400px;\r\n        overflow: hidden;\r\n    }\r\n    \r\n    .category-container .post-published {\r\n        font-size: 14px;\r\n        font-style: normal;\r\n        font-weight: bold;\r\n        text-transform: uppercase;\r\n    }\r\n    \r\n    .category-container .category-title {\r\n        font-size: 18px;\r\n        text-transform: uppercase;\r\n    }\r\n</style>\r\n<main class=\"category-container\">\r\n    {{#if page.results.category_banner}}\r\n        <div class=\"category-banner\">\r\n            <img src=\"{{page.results.category_banner}}\" width=\"100%\" />\r\n        </div>\r\n    {{/if}}\r\n    <div class=\"container\">\r\n        <div class=\"row mt-3\">\r\n            <div class=\"col-md-8\">\r\n                <h1 class=\"category-title\">{{_ \'Category: %s\' page.results.category_title}}</h1>\r\n\r\n                {{#each page.results.rows}}\r\n                    <hr />\r\n                    <em class=\"post-published\">{{date post_published \'F d, Y\'}}</em>\r\n                    <h3 class=\"post-title mt-2\">\r\n                        <a class=\"post-link\" href=\"/post/detail/{{post_slug}}\">\r\n                            {{post_title}}\r\n                        </a>\r\n                    </h3>\r\n                    <p class=\"post-summary\">{{post_summary}}</p>\r\n                {{/each}}\r\n            </div>\r\n            <div class=\"col-md-4\">\r\n                {{{block \'post-recent\'}}}\r\n\r\n                {{{block \'post-comments\'}}}\r\n\r\n                {{{block \'post-archives\'}}}\r\n\r\n                {{{block \'post-categories\'}}}\r\n            </div>\r\n        </div>\r\n    </div>\r\n</main>\r\n', '/images/default-image.png', 'app', 'text/html'),
(6, 'Post Archives', 'post/archives/:year/:month', 'This page is for showing posts under a year and month archive.', '[\"post search\"]', 1, '2019-02-11 12:14:49', '2019-02-12 11:45:36', 'post-archive-detail', '{\"sort\": {\"post_published\": \"DESC\"}, \"filter\": {\"post_active\": 1, \"post_status\": \"approved\"}}', '<style>\r\n    .post-container .post-published {\r\n        font-size: 14px;\r\n        font-style: normal;\r\n        font-weight: bold;\r\n        text-transform: uppercase;\r\n    }\r\n    \r\n    .post-container h1 {\r\n        font-size: 18px;\r\n        text-transform: uppercase;\r\n    }\r\n</style>\r\n<main class=\"post-container\">\r\n    <div class=\"container\">\r\n        <div class=\"row mt-3\">\r\n            <div class=\"col-md-8\">\r\n                <h1>{{#_ \'Archive: %s\'}}{{date page.results.archive \'F Y\'}}{{/_}}</h1>\r\n\r\n                {{#each page.results.rows}}\r\n                    <hr />\r\n                    <em class=\"post-published\">{{date post_published \'F d, Y\'}}</em>\r\n                    <h3 class=\"post-title mt-2\">\r\n                        <a class=\"post-link\" href=\"/post/detail/{{post_slug}}\">\r\n                            {{post_title}}\r\n                        </a>\r\n                    </h3>\r\n                    <p class=\"post-summary\">{{post_summary}}</p>\r\n                {{/each}}\r\n            </div>\r\n            <div class=\"col-md-4\">\r\n                {{{block \'post-recent\'}}}\r\n\r\n                {{{block \'post-comments\'}}}\r\n\r\n                {{{block \'post-archives\'}}}\r\n\r\n                {{{block \'post-categories\'}}}\r\n            </div>\r\n        </div>\r\n    </div>\r\n</main>\r\n', '/images/default-image.png', 'app', 'text/html'),
(7, 'Sitemap for Posts', 'post/sitemap-:start.xml', 'This is a sample sitemap template', '[\"sitemap\"]', 1, '2019-02-06 11:01:00', '2019-02-11 19:59:58', 'system-collection-search', '{\"sort\": {\"post_published\": \"DESC\"}, \"filter\": {\"post_active\": 1, \"post_status\": \"approved\"}, \"schema\": \"post\"}', '<?xml version=\"1.0\" encoding=\"UTF-8\"?>\r\n\r\n<urlset xmlns=\"http://www.sitemaps.org/schemas/sitemap/0.9\">\r\n    {{#each page.results.rows~}}\r\n    <url>\r\n        <loc>http://{{request \'server\' \'HTTP_HOST\'}}/post/{{post_slug}}</loc>\r\n        <lastmod>{{date post_updated \'Y-m-d\'}}</lastmod>\r\n        <changefreq>monthly</changefreq>\r\n        <priority>0.8</priority>\r\n    </url>\r\n    {{~/each}}\r\n</urlset> ', '/images/default-image.png', NULL, 'text/xml;charset=utf-8'),
(8, 'RSS Feed for Posts', 'post/feed.rss', 'This is a sample RSS feed', '[\"sitemap\"]', 1, '2019-02-09 17:32:36', '2019-02-11 20:00:18', 'system-collection-search', '{\"sort\": {\"post_published\": \"DESC\"}, \"range\": 25, \"filter\": {\"post_active\": 1, \"post_status\": \"approved\"}, \"schema\": \"post\"}', '<rss version=\"2.0\">\r\n    <channel>\r\n        <title>{{settings \'name\'}}</title>\r\n        <link>http://{{request \'server\' \'HTTP_HOST\'}}/</link>\r\n        <description>{{settings \'description\'}}</description>\r\n        <image>\r\n            <title>{{settings \'name\'}}</title>\r\n            <url>http://{{request \'server\' \'HTTP_HOST\'}}/images/incept/logo-long.png</url>\r\n            <link>http://{{request \'server\' \'HTTP_HOST\'}}/</link>\r\n        </image>\r\n        {{#each page.results.rows~}}\r\n        <item>\r\n            <title><![CDATA[{{post_title}}]]></title>\r\n            <link><![CDATA[http://{{request \'server\' \'HTTP_HOST\'}}/detail/{{post_slug}}]]></link>\r\n            <guid>{{post_id}}</guid>\r\n            <pubDate>{{date post_published \'r\'}}</pubDate>\r\n            <description><![CDATA[{{post_summary}}]]></description>\r\n            {{#if category_name}}\r\n                <category>{{category_name}}</category>\r\n            {{/if}}\r\n        </item>\r\n        {{~/each}}\r\n    </channel>\r\n</rss>', '/images/default-image.png', NULL, 'text/xml;charset=utf-8');
